import{_ as p,c as a,o as e,e as s}from"./app.f7d9e32b.js";const j=JSON.parse('{"title":"day1","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4E00. js\u8BFE\u7A0B\u9636\u6BB5\u8BF4\u660E","slug":"\u4E00-js\u8BFE\u7A0B\u9636\u6BB5\u8BF4\u660E","link":"#\u4E00-js\u8BFE\u7A0B\u9636\u6BB5\u8BF4\u660E","children":[]},{"level":2,"title":"\u4E8C.day1\u5B66\u4E60\u76EE\u6807","slug":"\u4E8C-day1\u5B66\u4E60\u76EE\u6807","link":"#\u4E8C-day1\u5B66\u4E60\u76EE\u6807","children":[]}],"relativePath":"guild/JSday1.md"}'),d={name:"guild/JSday1.md"},t=s('<h1 id="day1" tabindex="-1">day1 <a class="header-anchor" href="#day1" aria-hidden="true">#</a></h1><h2 id="\u4E00-js\u8BFE\u7A0B\u9636\u6BB5\u8BF4\u660E" tabindex="-1">\u4E00. js\u8BFE\u7A0B\u9636\u6BB5\u8BF4\u660E <a class="header-anchor" href="#\u4E00-js\u8BFE\u7A0B\u9636\u6BB5\u8BF4\u660E" aria-hidden="true">#</a></h2><p>(1) web\u6807\u51C6</p><p>\u5728 \u57FA\u7840\u73ED\u9636\u6BB5\u5B66\u4E60\u4E86Html\u5185\u5BB9\u548Ccss\u7684PC\u7AEF\u5185\u5BB9;</p><p>\u5728 \u79FB\u52A8web\u9636\u6BB5\u5B66\u4E60\u4E86css\u7684\u79FB\u52A8\u7AEF\u5185\u5BB9;</p><p>\u90A3\u6211\u4EEC\u7684web\u6807\u51C6 , \u7ED3\u6784 \u6837\u5F0F \u884C\u4E3A ,\u5C31\u57FA\u672C\u5B66\u5B8C2\u4E2A\u4E86;</p><p>\u63A5\u4E0B\u6765\u7684js\u9636\u6BB5 , \u5C31\u662F\u6211\u4EEC\u7684\u884C\u4E3A\u4E0E\u4EA4\u4E92;</p><p>(2) \u57FA\u7840\u9636\u6BB5\u7684\u5177\u4F53\u5B89\u6392</p><p>\u7B80\u5355\u4ECB\u7ECD5\u5929\u5185\u5BB9(\u4E3B\u8981\u8BB2\u89E3\u524D\u4E24\u5929\u5185\u5BB9,\u540E\u7EED\u51E0\u5929\u5B66\u5458\u76EE\u524D\u770B\u4E0D\u592A\u61C2,\u6CA1\u6709\u5173\u7CFB,\u6162\u6162\u6765)</p><p>\u660E\u786E\u9636\u6BB5\u7684\u76EE\u6807.</p><p>(3)\u57FA\u7840\u8BFE\u7A0B\u8BBE\u8BA1\u539F\u5219</p><p>\u200B 1.\u63A5\u4E0B\u6765\u7684\u5185\u5BB9\u5462 , \u5C31\u4E0D\u4F1A\u548Ccss\u4E00\u6837,\u8BB2\u5F88\u591A\u5B9E\u9645\u5F00\u53D1\u4E2D\u4E0D\u592A\u7528\u5F97\u5230\u7684\u4E1C\u897F\u4E86, \u56E0\u4E3Ajs\u7684\u5185\u5BB9\u8FD8\u662F\u6BD4\u8F83\u591A\u7684,\u6240\u4EE5\u6211\u4EEC\u4EE5\u5B9E\u7528\u4E3A\u4E3B , \u8BB2\u89E3\u5B9E\u9645\u5F00\u53D1\u4E2D\u6700\u6709\u4EF7\u503C\u7684\u5185\u5BB9 ,\u8BA9\u6211\u4EEC\u7684\u7CBE\u529B, \u5F97\u5230\u66F4\u4E3A\u5408\u7406\u7684\u5206\u914D;</p><p>\u200B 2.\u6BCF\u5929\u7684\u6700\u540E\u5462,\u90FD\u4F1A\u4E00\u4E2A\u7EFC\u5408\u6848\u4F8B, \u8FD9\u4E2A\u7EFC\u5408\u6848\u4F8B\u5462,\u6BCF\u5929\u90FD\u8981\u6C42\u5927\u5BB6\u80FD\u591F\u72EC\u7ACB\u5B8C\u6210 , \u6240\u4EE5\u5BF9\u5927\u5BB6\u7684\u8981\u6C42\u5176\u5B9E\u7B97\u662F\u5F88\u9AD8\u7684, \u540E\u7EED\u5462,\u4E5F\u4F1A\u51C6\u5907\u5728\u7EBF\u6D4B\u8BD5\u548C\u989D\u5916\u4F5C\u4E1A\u5F3A\u5316 ,\u8BA9\u5927\u5BB6\u5DE9\u56FA\u77E5\u8BC6\u70B9 , \u4E0D\u4EC5\u662F\u542C\u61C2\u4E86 ,\u8FD8\u8981\u4F1A\u7528.</p><p>\u200B 3.\u90E8\u5206\u7684\u8BFE\u5802\u6848\u4F8B\u548C\u8FD9\u4E2A\u7EFC\u5408\u6848\u4F8B\u5462 , \u524D\u9762\u6211\u53EF\u80FD\u4F1A\u5148\u5E26\u7740\u5927\u5BB6\u5206\u6790, \u518D\u4E00\u8D77\u53BB\u505A, \u540E\u9762\u5C31\u4F1A\u5C3D\u91CF\u8981\u6C42\u5927\u5BB6\u72EC\u7ACB\u5B8C\u6210\u4E86,\u8BA9\u5927\u5BB6\u5C3D\u65E9\u57F9\u517B\u51FA\u72EC\u7ACB\u601D\u8003\u548C\u7F16\u7A0B\u601D\u7EF4.</p><p>\u200B 4.\u6574\u4E2A\u9636\u6BB5\u7684\u8BB2\u89E3\u4F1A\u504F\u6162\u548C\u7EC6 , \u56E0\u4E3A\u8FD9\u4E2A\u9636\u6BB5\u7279\u522B\u91CD\u8981\u554A, \u5E0C\u671B\u5927\u5BB6\u80FD\u591F\u90FD\u987A\u5229\u7684\u628A\u8FD9\u4E2A\u9636\u6BB5\u7684\u5185\u5BB9\u5B66\u4F1A, \u624D\u80FD\u66F4\u5230\u7684\u6B65\u5165\u540E\u7EED\u7684\u9636\u6BB5, \u6211\u4EEC\u7684\u8BFE\u7A0B\u90FD\u662F\u73AF\u73AF\u76F8\u6263\u7684, js\u662F\u6211\u4EEC\u524D\u7AEF\u7684\u6839\u672C , \u5C31\u8FD9\u4E48\u4E00\u95E8\u8BED\u8A00, \u6240\u4EE5\u6211\u4EEC\u4E00\u5B9A\u8981\u597D\u597D\u7684 , \u8BA4\u771F\u7684\u5B66\u4E60\u5B83 .</p><p>\u200B -- end</p><h2 id="\u4E8C-day1\u5B66\u4E60\u76EE\u6807" tabindex="-1">\u4E8C.day1\u5B66\u4E60\u76EE\u6807 <a class="header-anchor" href="#\u4E8C-day1\u5B66\u4E60\u76EE\u6807" aria-hidden="true">#</a></h2><p>(1)\u5B66\u4E60\u76EE\u6807</p><p>\u4ECA\u5929\u7684\u5185\u5BB9\u6709\u5F88\u591A,\u4F46\u662F\u91CC\u9762\u67093\u4E2A\u91CD\u70B9;</p><p>\u53D8\u91CF\\\u6570\u636E\\\u6570\u636E\u7C7B\u578B\u7684\u8F6C\u6362\u4E0E\u7279\u5F81</p><p>\u8BDD\u5728\u8BF4\u5728\u524D\u9762,\u4ECA\u5929\u7684\u90E8\u5206\u5185\u5BB9\u5C31\u6709\u70B9\u50CF\u5F53\u521D\u6211\u4EEC\u5B66\u4E60div span ,\u7136\u540E\u544A\u8BC9\u5927\u5BB6\u4EC0\u4E48\u662F\u884C\u5185,\u4EC0\u4E48\u662F\u5757\u7EA7,\u4EC0\u4E48\u662F\u884C\u5185\u5757</p><p>\u7136\u540E\u8FD8\u8BF4\u4E86\u4EC0\u4E48\u53EB\u884C\u5185\u5757\u7684\u8F6C\u6362 , \u8BB2\u5B8C\u77E5\u8BC6\u70B9\u5F53\u5929\u7684\u5927\u5BB6\u662F\u4E0D\u662F\u8FD8\u6709\u70B9\u61F5\u61F5\u7684, \u4F46\u662F\u540E\u9762\u7528\u7740\u7528\u7740\u662F\u4E0D\u662F\u5C31\u4ECE\u6765\u4E0D\u89C9\u5F97\u90A3\u662F\u4E2A\u96BE\u70B9\u4E86.</p><p>\u4ECA\u5929\u7684\u77E5\u8BC6\u70B9\u5462, \u5C31\u6709\u70B9\u7C7B\u4F3C\u4E8E\u8FD9\u6837 , \u5927\u5BB6\u5148\u6B63\u5E38\u53BB\u7406\u89E3, \u6211\u5C3D\u91CF\u628A\u4ED6\u8BB2\u7684\u6613\u61C2 ,\u4E14\u4E0D\u626F\u591A\u4E86,\u514D\u5F97\u5927\u5BB6\u60F3\u7684\u8D1F\u8D23, \u672C\u8EAB\u5C31\u662F\u4E00\u4E2A\u5F88\u7B80\u5355\u7684\u7684\u6982\u5FF5 ,\u5927\u5BB6\u8981\u8BB0\u4F4F\u8FD9\u53E5\u8BDD\u54E6.</p><p>\u6B63\u5E38\u7406\u89E3, \u540E\u7EED\u5929\u5929\u90FD\u7528 , \u5B8C\u5168\u4E0D\u62C5\u5FC3\u5F04\u4E0D\u61C2;</p><p>(2)JavaScript\u662F\u4EC0\u4E48?</p><p>\u4E00\u53E5\u8BDD: js\u662F\u4E00\u79CD\u8FD0\u884C\u5728\u6D4F\u89C8\u5668\u7684\u7F16\u7A0B\u8BED\u8A00(\u811A\u672C\u8BED\u8A00),\u4E3B\u8981\u7528\u4E8E\u5B9E\u73B0\u9875\u9762\u4EA4\u4E92\u6548\u679C.</p><p>js\u53EF\u4EE5\u505A\u54EA\u4E9B\u4E8B\u5462?</p><p>\u7F51\u9875\u6548\u679C(\u9F20\u6807\u6EDA\u52A8,\u9F20\u6807\u70B9\u51FB,\u76D1\u542C\u7528\u6237\u884C\u4E3A)</p><p>\u8868\u5355\u9A8C\u8BC1(\u8F93\u5165\u662F\u5426\u5408\u6CD5,\u5408\u89C4\u77E9)</p><p>\u6570\u636E\u4EA4\u4E92(\u4E0E\u540E\u7AEF\u6253\u4EA4\u9053 , \u5B58\u53D6\u6570\u636E , \u6E32\u67D3\u5230\u524D\u7AEF\u9875\u9762)</p><p>\u670D\u52A1\u7AEF\u8FB9\u6846(node.js)</p><p>(3)JavaScript\u7684\u7EC4\u6210</p><p>JavaScript\u5206\u4E3A2\u4E2A\u90E8\u5206</p><p>ECMA\u548Cwebapis</p><p>\u524D\u8005\u662F\u6211\u4EEC\u8981\u5B66\u4E60\u7684\u57FA\u672C\u89C4\u8303, \u4F46\u662F\u5927\u591A\u90FD\u662F\u5355\u4E00\u77E5\u8BC6\u70B9 , \u6211\u4EEC\u53EA\u80FD\u7528\u8FD9\u4E9B\u77E5\u8BC6\u70B9\u7EC4\u6210\u4E00\u4E9B\u6BD4\u8F83\u5355\u4E00\u7684\u5C0F\u6848\u4F8B</p><p>\u540E\u8005\u5462\u624D\u662F\u548C\u6D4F\u89C8\u5668\u7684\u4EA4\u4E92\u65B9\u6CD5 , \u540E\u8005\u7684\u5185\u5BB9\u9700\u8981\u524D\u8005\u4F5C\u4E3A\u57FA\u7840</p><p>\u6700\u540E\u4E24\u8005\u642D\u914D\u5728\u4E00\u8D77, \u624D\u53EF\u4EE5\u5B8C\u6574\u7684\u5B9E\u73B0\u5BF9\u6D4F\u89C8\u5668\u9875\u9762\u5143\u7D20\u7684\u64CD\u4F5C</p><p>\u4E3E\u4E2A\u6817\u5B50: \u6211\u8981\u505A\u4E00\u4E2A\u8BA1\u7B97\u5668, \u4F46\u662F\u6211\u53EA\u7528\u524D\u9762\u7684\u77E5\u8BC6\u70B9\u5462 , \u53EA\u80FD\u5199\u4E00\u4E2A\u5F88\u4E11\u7684\u6CA1\u6709\u5916\u89C2\u7684\u53EA\u6709\u903B\u8F91\u7684\u8BA1\u7B97\u5668\u529F\u80FD,</p><p>\u800C\u7528\u4E0A\u540E\u8005\u7684webapis\u77E5\u8BC6\u70B9\u5462, \u6211\u5C31\u53EF\u4EE5\u628A\u4ED6\u5728\u7F51\u9875\u4E0A\u7528css\u5199\u4E00\u4E2A\u8BA1\u7B97\u5668\u7684\u6548\u679C,\u5E76\u4E14\u628A\u6BCF\u4E2A\u6309\u94AE\u90FD\u7ED1\u5B9A\u4E0A\u76F8\u5E94\u7684\u64CD\u4F5C, \u6211\u7684\u8BA1\u7B97\u673A\u5C31\u4F1A\u597D\u770B\u5F88\u591A ,\u4E14\u7528\u6237\u4F53\u9A8C\u4E5F\u5927\u5927\u63D0\u5347.</p><p>\u90A3\u6211\u4EEC\u8FD9\u4E2A\u9636\u6BB5\u8981\u5B66\u7684\u5462 , \u5C31\u5148\u4E0D\u8003\u8651\u597D\u4E0D\u597D\u770B, \u7F8E\u4E0D\u7F8E\u89C2, \u5148\u4E0D\u8003\u8651\u7528\u6237\u4F53\u9A8C, \u53EA\u662F\u5355\u7EAF\u7684\u628A\u903B\u8F91\u7ED9\u8DD1\u901A , \u903B\u8F91\u80FD\u591F\u6B63\u5E38\u4E00\u6B65\u4E00\u6B65\u7684\u6267\u884C.</p><p>(4)JavaScript\u4E66\u5199\u4F4D\u7F6E</p>',41),r=[t];function i(c,n,_,l,o,h){return e(),a("div",null,r)}const u=p(d,[["render",i]]);export{j as __pageData,u as default};
